import React, { Component } from 'react';
import NavBar from './components/navbar';
import './App.css';
import Counters from './components/counters';

class App extends Component {
  state = {
    counters :[
        {id :1,value:4},
        {id:2,value :0},
        {id:3,value:0},
        {id:4,value:0}
    ]
  }
  handleIncrement =counter=>{
      const counters=[...this.state.counters];
      const index=counters.indexOf(counter);
      counters[index]={...counter};
      counters[index].value++;
      this.setState({ counters });



  };
  handleReset =()=>{
     const counters= this.state.counters.map(c=> {c.value=0;
    return c;});
    this.setState({counters});
  };
  handleDelete= countersId =>{
      console.log(countersId);
      const counters= this.state.counters.filter(c=> c.id !== countersId);
      this.setState({counters :counters});

  };
  render() {
    return (
      <React.Fragment>
      <NavBar totalCounters={this.state.counters.filter(c => c.value > 0).length} />
      <main className="container">
      <Counters counters={this.state.counters} onDelete={this.handleDelete} onIncrement={this.handleIncrement} onReset={this.handleReset}/>
      </main>
      </React.Fragment>

    );
  }
}

export default App;
